        n //= 2
        res += n
    return res + n * (n - 1) // 2

def check(n, q):
    if 2 ** q - 1 > n:
        return None
    left = 0
    right = 10 ** 10
    f = lambda k : k * (k - 1) // 2 + k * (2 ** q - 1)
    while left + 1 < right:
        mid = (left + right) // 2
        if f(mid) <= n:
            left = mid
        else:
            right = mid
    count = left * 2**q
    if calc(count) == n:
        return count
    else:
        return None

