    last = None
    for c in str:
        if last is None:
            last = c
            continue

        if not isVowel(last) and last != 'n' and not isVowel(c):
            return 'NO'

        last = c

    if not isVowel(last) and last != 'n':
        return 'NO'

    return 'YES'

