def poss(ss):
    prr = [0 for i in range(26)]
    ques = 0
    for x in ss:
        if(x=='?'):
            ques += 1
        else:
            prr[ord(x)-65] = 1
    return (sum(prr)+ques)==26

def make(ss):
    prr = [0 for i in range(26)]
    for x in ss:
        if(x!='?'):
            prr[ord(x)-65] = 1
    ans = ''
    for x in ss:
        if(x=='?'):
