def scand():
    return int(input())
def scana():
    return [int(x) for x in input().split()]
n=scand()
a=scana()
summ=sum(a)
for i in range(max(a),10000):
    if(i*n-summ>summ):
        print(i)
        break

def ii():
    return int(input())
def mi():
    return map(int, input().split())
def li():
    return list(mi())

n = ii()
a = li()
s = sum(a)
k = max(a)
while 1:
    if n * k - s > s:
        print(k)
        break
    k += 1

n=int(input())
