n = int(input())
a = list(map(int, input().split()))

maxd = 0
for i in range(n):
    if a[i] <= 500000:
        maxd = max(maxd, a[i] - 1)
    else:
        maxd = max(maxd, 1000000 - a[i])
print(maxd)

def ii():
    return int(input())
def mi():
    return map(int, input().split())
def li():
    return list(mi())

n = ii()
a = li()
m = 10 ** 6 // 2
x = 0
y = 10 ** 6
for ai in a:
    if ai <= m:
        x = max(x, ai)
    else:
        y = min(y, ai)
ans = max(x - 1, 10 ** 6 - y)
print(ans)
