def main():
    from sys import stdin, stdout
    (m, n) = list(map(int, stdin.readline().split()))
    minim = min(m, n)
    stdout.write(str(minim + 1) + '\n')
    if n == minim:
        for i in range(minim + 1):
            stdout.write(str(m) + ' ' + str(i) + '\n')
            m -= 1
    else:
        for i in range(minim + 1):
            stdout.write(str(i) + ' ' + str(n) + '\n')
            n -= 1


def __starting_point():
    main()


__starting_point()
