n = int(input())
t = input()
s, x = 0, t[0]
for i in range(1, n):
    if t[i] == x:
        s += 1
    else:
        x = t[i]
print(s)

def stones():
    datain = []
    removed = 0
    for i in range(2):
        datain.append(input())    
    colors = datain[1]

    for i in range(len(colors)):
        if i == len(colors)-1:
            break
        else:
            if colors[i] == colors[i+1]:
                removed += 1

    print(removed)

stones()
