def prefix(st):
    t = 0
    p = [0] * (len(st) + 1)
    o = [0] * (len(st) + 1)
    for i in range(2, len(st)):
        while t > 0 and st[i] != st[t + 1]:
            t = p[t]
        if st[i] == st[t + 1]:
            t += 1
        p[i] = t
    while t > 0:
        o[t] = 1
        t = p[t]
    return o


