def vector(a, b, c, d):
    return([c - a, d - b])

def cross_product(a, b):
    return(a[0] * b[1] - a[1] * b[0])

def dot_product(a, b):
    return(a[0] * b[0] + a[1] * b[1])

def bin_search(lo, hi):
    while (hi - lo > 1):
        mid = (lo + hi) // 2;
        if cross_product(vector(0, mid, mid, 0), vector(0, mid, x1, y1)) < EPS:
            hi = mid
        else:
            lo = mid
    return(hi)


