# from typing import *

MOD = 998244353


# def solve(A: int, B: int, C: int) -> int:
def solve(A, B, C):
    tmp = 1
    tmp *= A * (A + 1) // 2
    tmp %= MOD
    tmp *= B * (B + 1) // 2
    tmp %= MOD
    tmp *= C * (C + 1) // 2
    tmp %= MOD
    return tmp


