def solve(a, b, t1, f1, t2, f2):
    if t1 == t2:
        return abs(f1 - f2)
    elif (f1 >= a and f1 <= b) or (f2 >= a and f2 <= b):
        return abs(t1 - t2) + abs(f1 - f2)
    elif f1 > b and f2 > b:
        return abs(t1 - t2) + f1 + f2 - 2 * b
    elif f1 < a and f2 < a:
        return abs(t1 - t2) + 2 * a - f1 - f2
    else:
        return abs(t1 - t2) + abs(f1 - f2)

