sys.setrecursionlimit(10000)
mod = 1000000007


def combi(n, r):
    if r == 0 or r == n:
        return 1
    if r < 0 or n < r:
        return 0
    return (((facts[n] * facts_inv[r]) % mod) * facts_inv[n - r]) % mod


def solve(n, m, first_time=True):
    if n == "1":
        return 1 if (m == 0 or m == 1) else 0
    if m < 0 or len(n) < m:
        return 0
    if m == 0:
        return 1
    ind = n.find("1", 1)
    if ind == -1:
        if first_time:
            return combi(len(n) - 1, m) % mod
        else:
            return len(n) if m == 1 else combi(len(n) - 1, m) % mod
    else:
        if first_time and m == 1:
            return (solve(n[ind:], m - 1, False) + combi(len(n) - 1, m) - 1) % mod
        return (solve(n[ind:], m - 1, False) + combi(len(n) - 1, m)) % mod


n = input()
k = int(input())
if k > 5:
    print(0)
elif k == 0:
    print(1)
elif k == 1 and n == "1":
    print(0)
else:
    res = 0
    for m in ll[k - 1]:
        res += solve(n, m)
        res %= mod
    print(res)
