# B. The Fair Nut and Strings

n, k = mi()
s = input().strip()
t = input().strip()

ans = 0
jj = 0
for i in range(n):
    if s[i] == t[i]:
        ans += 1
        jj = i + 1
    else:
        break
cur = 2
for j in range(jj, n):
    if s[j] == 'b':
        cur -= 1
    if t[j] == 'a':
        cur -= 1
    if cur >= k:
        ans += k * (n - j)
        break
    ans += cur
    cur *= 2

print(ans)

import sys
readline = sys.stdin.readline

N, K = list(map(int, readline().split()))
S = [1 if s == 'b' else 0 for s in readline().strip()]
T = [1 if s == 'b' else 0 for s in readline().strip()]

diff = 0
for s, t in zip(S, T):
    if diff > K:
        break
    if s == t:
        diff <<= 1
    elif s == 1:
        diff = (diff << 1) - 1
    else:
        diff = (diff << 1) + 1

K = min(K, diff + 1)

ans = 0
cnt = 1
ans = 0
for s, t in zip(S, T):
    if cnt == K:
        ans += cnt
        continue
    if cnt == 1:
        if s == t:
            ans += cnt
        else:
            cnt += 1
            cnt = min(K, cnt)
            ans += cnt
        continue
    else:
        cnt *= 2
        if s == 1:
            cnt -= 1
        if t == 0:
            cnt -= 1
        cnt = min(K, cnt)
        ans += cnt

print(ans)
