class Solution:
     def countBattleships(self, board):
         """
         :type board: List[List[str]]
         :rtype: int
         """
         count = 0
         for i in range(len(board)):
             for j in range(len(board[i])):
                 if board[i][j] == 'X':
                     if i-1 < 0 and j-1 < 0:
                         count += 1
                     elif i-1 < 0 and board[i][j-1] != 'X':
                         count += 1
                     elif j-1 < 0 and board[i-1][j] != 'X':
                         count += 1
                     elif board[i-1][j] != 'X' and board[i][j-1] != 'X':
                         count += 1
         return count
class Solution:
     def countBattleships(self, board):
         """
         :type board: List[List[str]]
         :rtype: int
         """
         num=0

         record=[(-1,-1)]


         colnum=0
         for i in board:
             rownum=0
             cont=0
             for j in i:

                 if j=="X":
                       if (cont==1):
                             rownum+=1
                             continue
                       else:            
                              ind=0
                              for k in record:
                                   a,b=k
                                   if (a==colnum)&(b==rownum):
                                     ind=1
                                     record.append((a+1,b))
                                     record.remove((a,b))  
                                     break

                              if (ind==0):
                                     num=num+1
                                     record.append((colnum+1,rownum))
                                     cont=1
                 if j!="X":
                     cont=0
                 rownum+=1           


             colnum+=1

         return num

class Solution:
     def countBattleships(self, board):
         """
         :type board: List[List[str]]
         :rtype: int
         """
         ans = 0
         for r, row in enumerate(board):
             for c, val in enumerate(row):
                 if val == 'X':
                     ans += 1
                     if r and board[r-1][c] == 'X':
                         ans -= 1
                     elif c and board[r][c-1] == 'X':
                         ans -= 1
         return ans
