class Solution:
    def maskPII(self, S: str) -> str:
        if '@' in S:
            name, domain = S.split('@')
            return name[0].lower() + '*****' + name[-1].lower() + '@' + domain.lower()
        else:
            number = ''
            for c in S:
                if c.isdigit():
                    number += c
            if len(number) == 10:
                return '***-***-' + number[-4:]
            else:
                return '+' + '*'*(len(number)-10) + '-***-***-' + number[-4:]

class Solution:
    def maskPII(self, S: str) -> str:
        N=len(S)
        if '@' in S:
            S=S.lower()
            first,rest=S.split('@')
            return first[0]+'*'*5+first[-1]+'@'+rest
        else:
            digits=''.join(c for c in S if c.isdigit())
            a=[]
            if len(digits)>10:
                a.append('+'+'*'*(len(digits)-10))
            a.append('***')
            a.append('***')
            a.append(digits[-4:])
            return '-'.join(a)

class Solution:
    def maskPII(self, S: str) -> str:
        if '@' in S:
            s = S.split('@')
            s[1] = s[1].split('.')
            s[0] = s[0][0] + '*****' + s[0][-1]
            output = (s[0] + '@' + s[1][0] + '.' + s[1][1]).lower()
        else:
            nums = [i for i in S if i.isdigit()]
            if len(nums) == 10:
                output = f'***-***-{nums[-4]}{nums[-3]}{nums[-2]}{nums[-1]}'
            elif len(nums) == 11:
                output = f'+*-***-***-{nums[-4]}{nums[-3]}{nums[-2]}{nums[-1]}'
            elif len(nums) == 12:
                output = f'+**-***-***-{nums[-4]}{nums[-3]}{nums[-2]}{nums[-1]}'
            else:
                output = f'+***-***-***-{nums[-4]}{nums[-3]}{nums[-2]}{nums[-1]}'
        return output
