class Solution:
    def strWithout3a3b(self, A: int, B: int) -> str:
        if A >= 2*B:
            return 'aab'* B + 'a'* (A-2*B)
        elif A >= B:
            return 'aab' * (A-B) + 'ab' * (2*B - A)
        elif B >= 2*A:
            return 'bba' * A + 'b' *(B-2*A)
        else:
            return 'bba' * (B-A) + 'ab' * (2*A - B)
class Solution:
    def strWithout3a3b(self, A: int, B: int) -> str:
        count = [A, B]
        char = ['aab', 'bba']
        for i in range(2):
            j = i ^ 1
            if count[i] >= count[j]:
                if count[i] >= count[j] * 2:
                    return char[i] * count[j] + char[i][:1] * (count[i] - count[j]*2)
                return char[i] * (count[i] - count[j]) + char[i][1:] * (count[j]*2 - count[i])
class Solution:
    def strWithout3a3b(self, A: int, B: int) -> str:
        if A >= B:
            if A >= B * 2:
                return 'aab' * B + 'a' * (A - B*2)
            return 'aab'  * (A - B) + 'ab' * (B*2 - A)
        if B >= A * 2:
            return 'bba' * A + 'b' * (B - A*2)
        return 'bba'  * (B - A) + 'ba' * (A*2 - B)
