def get_prime_factors(number):
 if number == 1:
  return []

 for i in range(2, number):
  # Get remainder and quotient
  rd, qt = divmod(number, i)
  if not qt: # if equal to zero
   return [i] + get_prime_factors(rd)

 return [number]

