import math
def binarySearch (arr, l, r, x):
 mid = (l+r)//2
 if arr[mid] == x: 
  return mid
 elif arr[mid] > x: 
  return binarySearch(arr, l, mid-1, x) 
 elif (arr[mid]<x and arr[mid+1]>x):
  return mid
 else: 
  return binarySearch(arr, mid + 1, r, x)

def vceil(k):
 if(math.ceil(k)==k):
  return int(k+1)
 else:
  return math.ceil(k)
