L = 10**9 + 7

def mat_mult(lst1,lst2,r,c,m):
 res = [[0]*m for i in range(r)]
 for i in range(r):
  for j in range(m):
   res[i][j] = 0
   for k in range(c):
    res[i][j] = (res[i][j] + lst1[i][k]*lst2[k][j])%L

 return res


