    save = {' ': '%20', '!': '%21', '$': '%24', '%': '%25', '(': '%28', ')': '%29', '*': '%2a'}
    string = s().strip()
    while True:
        output = []
        if '#' in string:
            return
        for i in string:
            if i in save:
                output.append(save[i])
            else:
                output.append(i)
        print(''.join(output))
        string = s().strip()

