class Solution:

    def findNthDigit(self, n):
        """
        :type n: int
        :rtype: int
        """
        if n <= 0:
            return
        i = 0
        nbase = 0
        thres = 1 * 9
        if n <= thres:
            return n
        while 1:
            i += 1
            nbase += 9 * 10 ** (i - 1)
            thres += (i + 1) * 9 * 10 ** i
            if n <= thres:
                return int(str((n + i - (thres - (i + 1) * 9 * 10 ** i)) // (i + 1) + nbase)[(n + i - (thres - (i + 1) * 9 * 10 ** i)) % (i + 1)])
        '\n    #    my third solution....beat 93%....\n         if n <= 0:\n             return\n         \n         nbase = 0\n         thres = 1*9\n         if n <= thres:\n             return n\n         \n         nbase += 9*10**0\n         thres += 2*9*10**1\n         if n <= thres:\n             return int(str((n+1-(thres-2*9*10**1))//2 + nbase)[(n+1-(thres-2*9*10**1))%2])\n         \n         nbase += 9*10**1\n         thres += 3*9*10**2\n         if n <= thres:\n             return int(str((n+2-(thres-3*9*10**2))//3 + nbase)[(n+2-(thres-3*9*10**2))%3])\n         \n         nbase += 9*10**2\n         thres += 4*9*10**3\n         if n <= thres:\n             return int(str((n+3-(thres-4*9*10**3))//4 + nbase)[(n+3-(thres-4*9*10**3))%4])\n         \n         nbase += 9*10**3\n         thres += 5*9*10**4\n         if n <= thres:\n             return int(str((n+4-(thres-5*9*10**4))//5 + nbase)[(n+4-(thres-5*9*10**4))%5])\n         \n         nbase += 9*10**4\n         thres += 6*9*10**5\n         if n <= thres:\n             return int(str((n+5-(thres-6*9*10**5))//6 + nbase)[(n+5-(thres-6*9*10**5))%6])\n         \n         nbase += 9*10**5\n         thres += 7*9*10**6\n         if n <= thres:\n             return int(str((n+6-(thres-7*9*10**6))//7 + nbase)[(n+6-(thres-7*9*10**6))%7])\n         \n         nbase += 9*10**6\n         thres += 8*9*10**7\n         if n <= thres:\n             return int(str((n+7-(thres-8*9*10**7))//8 + nbase)[(n+7-(thres-8*9*10**7))%8])\n         \n         nbase += 9*10**7\n         thres += 9*9*10**8\n         if n <= thres:\n             return int(str((n+8-(thres-9*9*10**8))//9 + nbase)[(n+8-(thres-9*9*10**8))%9])\n         '
        '\n    #    my second solution........beat 96%....\n         if n <= 0:\n             return\n         \n         if n <= 1*9:\n             return n\n         \n         if n <= 1*9+2*90:\n             return int(str((n+1 - 1*9)//2 + 9)[(n+1 - 1*9)%2])\n         \n         if n <= 1*9+2*90+3*900:\n             return int(str((n+2 - 1*9-2*90)//3 + 99)[(n+2 - 1*9-2*90)%3])\n         \n         if n <= 1*9+2*90+3*900+4*9000:\n             return int(str((n+3 - 1*9-2*90-3*900)//4 + 999)[(n+3 - 1*9-2*90-3*900)%4])\n         \n         if n <= 1*9+2*90+3*900+4*9000+5*90000:\n             return int(str((n+4 - 1*9-2*90-3*900-4*9000)//5 + 9999)[(n+4 - 1*9-2*90-3*900-4*9000)%5])\n         \n         if n <= 1*9+2*90+3*900+4*9000+5*90000+6*900000:\n             return int(str((n+5 - 1*9-2*90-3*900-4*9000-5*90000)//6 + 99999)[(n+5 - 1*9-2*90-3*900-4*9000-5*90000)%6])\n         \n         if n <= 1*9+2*90+3*900+4*9000+5*90000+6*900000+7*9000000:\n             return int(str((n+6 - 1*9-2*90-3*900-4*9000-5*90000-6*900000)//7 + 999999)[(n+6 - 1*9-2*90-3*900-4*9000-5*90000-6*900000)%7])\n         \n         if n <= 1*9+2*90+3*900+4*9000+5*90000+6*900000+7*9000000+8*90000000:\n             return int(str((n+7 - 1*9-2*90-3*900-4*9000-5*90000-6*900000-7*9000000)//8 + 9999999)[(n+7 - 1*9-2*90-3*900-4*9000-5*90000-6*900000-7*9000000)%8])\n         \n         if n <= 1*9+2*90+3*900+4*9000+5*90000+6*900000+7*9000000+8*90000000+9*900000000:\n             return int(str((n+8 - 1*9-2*90-3*900-4*9000-5*90000-6*900000-7*9000000-8*90000000)//9 + 99999999)[(n+8 - 1*9-2*90-3*900-4*9000-5*90000-6*900000-7*9000000-8*90000000)%9])\n         '
        '\n    #    my first solution...........beat 93%.....\n         if n <= 0:\n             return\n         \n         if n <= 1*9:\n             return n\n         \n         if n <= 1*9+2*90:\n             return int(str((n+1 - 1*9)//2 + 9)[n%2])\n         \n         if n <= 1*9+2*90+3*900:\n             return int(str((n+2 - 1*9-2*90)//3 + 99)[n%3-1])\n         \n         if n <= 1*9+2*90+3*900+4*9000:\n             return int(str((n+3 - 1*9-2*90-3*900)//4 + 999)[n%4-2])\n         \n         if n <= 1*9+2*90+3*900+4*9000+5*90000:\n             return int(str((n+4 - 1*9-2*90-3*900-4*9000)//5 + 9999)[n%5])\n         \n         if n <= 1*9+2*90+3*900+4*9000+5*90000+6*900000:\n             return int(str((n+5 - 1*9-2*90-3*900-4*9000-5*90000)//6 + 99999)[n%6-4])\n         \n         if n <= 1*9+2*90+3*900+4*9000+5*90000+6*900000+7*9000000:\n             return int(str((n+6 - 1*9-2*90-3*900-4*9000-5*90000-6*900000)//7 + 999999)[n%7])\n         \n         if n <= 1*9+2*90+3*900+4*9000+5*90000+6*900000+7*9000000+8*90000000:\n             return int(str((n+7 - 1*9-2*90-3*900-4*9000-5*90000-6*900000-7*9000000)//8 + 9999999)[n%8-2])\n         \n         if n <= 1*9+2*90+3*900+4*9000+5*90000+6*900000+7*9000000+8*90000000+9*900000000:\n             return int(str((n+8 - 1*9-2*90-3*900-4*9000-5*90000-6*900000-7*9000000-8*90000000)//9 + 99999999)[n%9-1])\n         '
