def blocks_to_collect(x):
    return {"total": (4*(x**3)+12*(x**2)+11*x)//3,"gold": (64*((1+(x-1)//4)**3)-24*((1+(x-1)//4)**2)-13*(1+(x-1)//4))//3,"diamond": (64*((1+(x-2)//4)**3)+24*((1+(x-2)//4)**2)-13*(1+(x-2)//4))//3,"emerald": (64*((1+(x-3)//4)**3)+72*((1+(x-3)//4)**2)+11*(1+(x-3)//4))//3,"iron": (64*((1+(x-4)//4)**3)+120*((1+(x-4)//4)**2)+59*(1+(x-4)//4))//3}
