def multi_table(number):
    return "1 * " + str(number) + " = " + str(1 * number) + "\n" + "2 * " + str(number) + " = " + str(2 * number) + "\n" + "3 * " + str(number) + " = " + str(3 * number) + "\n" + "4 * " + str(number) + " = " + str(4 * number) + "\n" + "5 * " + str(number) + " = " + str(5 * number) + "\n" + "6 * " + str(number) + " = " + str(6 * number) + "\n" + "7 * " + str(number) + " = " + str(7 * number) + "\n" + "8 * " + str(number) + " = " + str(8 * number) + "\n" + "9 * " + str(number) + " = " + str(9 * number) + "\n" + "10 * " + str(number) + " = " + str(10 * number)
