def multi_table(number):
    return '1 *' + ' ' + str(number) + ' = ' + str(number) + '\n2 *' + ' ' + str(number) + ' = ' + str(number * 2) + '\n3 *' + ' ' + str(number) + ' = ' + str(number * 3) + '\n4 *' + ' ' + str(number) + ' = ' + str(number * 4) + '\n5 *' + ' ' + str(number) + ' = ' + str(number * 5) + '\n6 *' + ' ' + str(number) + ' = ' + str(number * 6) + '\n7 *' + ' ' + str(number) + ' = ' + str(number * 7) + '\n8 *' + ' ' + str(number) + ' = ' + str(number * 8) + '\n9 *' + ' ' + str(number) + ' = ' + str(number * 9) + '\n10 *' + ' ' + str(number) + ' = ' + str(number * 10)
