def correct_polish_letters(st):
    if not isinstance(st, str):
        st = str(st)
    return ''.join((NON_NFKD_MAP[c] if c in NON_NFKD_MAP else c for part in unicodedata.normalize('NFKD', st) for c in part if unicodedata.category(part) != 'Mn'))
