def gym_slang(phrase):
    return phrase.replace('probably', 'prolly').replace('Probably', 'Prolly').replace('i am', "i'm").replace('I am', "I'm").replace('instagram', 'insta').replace('Instagram', 'Insta').replace('do not', "don't").replace('Do not', "Don't").replace('going to', 'gonna').replace('Going to', 'Gonna').replace('combination', 'combo').replace('Combination', 'Combo')
