def segment_display(num):
    temp=str(num).rjust(6, "_")
    numbers=[[' ### ', '     ', ' ### ', ' ### ', '     ', ' ### ', ' ### ', ' ### ', ' ### ', ' ### '], ['#   #', '    #', '    #', '    #', '#   #', '#    ', '#    ', '    #', '#   #', '#   #'], ['#   #', '    #', '    #', '    #', '#   #', '#    ', '#    ', '    #', '#   #', '#   #'], ['#   #', '    #', '    #', '    #', '#   #', '#    ', '#    ', '    #', '#   #', '#   #'], ['     ', '     ', ' ### ', ' ### ', ' ### ', ' ### ', ' ### ', '     ', ' ### ', ' ### '], ['#   #', '    #', '#    ', '    #', '    #', '    #', '#   #', '    #', '#   #', '    #'], ['#   #', '    #', '#    ', '    #', '    #', '    #', '#   #', '    #', '#   #', '    #'], ['#   #', '    #', '#    ', '    #', '    #', '    #', '#   #', '    #', '#   #', '    #'], [' ### ', '     ', ' ### ', ' ### ', '     ', ' ### ', ' ### ', '     ', ' ### ', ' ### ']]
    res=[]
    for i in range(len(numbers)):
        string=[]
        for j, num in enumerate(temp):
            string.append(" "*5) if num=="_" else string.append(numbers[i][int(num)])
        res.append("| "+" | ".join(string)+" |")
    return "\n".join(res)
