def leaderboard_sort(l, c): return (lambda u: (([([0 for u['i'] in [u['d'][o.split()[0]] - int(o.split()[1])]], [(([0 for u['d'][k] in [u['d'][k] + 1]] if u['d'][o.split()[0]] > v >= u['i'] else None), ([0 for u['d'][k] in [u['d'][k] - 1]] if u['d'][o.split()[0]] < v <= u['i'] else None)) for k, v in u['d'].items()], [None for u['d'][o.split()[0]] in [u['i']]]) for o in c], sorted(u['d'].keys(), key=lambda x: u['d'][x]))[1]))({'d': {l[i]: i for i in range(len(l))}, 'i': 0})
