def pattern(n, x=1, w=1, e=1, r=1, y=1, u=1): return "\n".join([(lambda r: "".join(r) + "".join(["".join(r[1:]) for j in range(1, x)]))([" "] * (i - 1) + [str(i % 10)] + [" "] * (2 * (n - i) - 1) + [str(i % 10)] + [" "] * (i - 1)) for i in range(1, n)] + [(lambda r: r + "".join([r[1:] for j in range(1, x)]))(" " * (n - 1) + str(n % 10) + " " * (n - 1))] + [(lambda r: "".join(r) + "".join(["".join(r[1:]) for j in range(1, x)]))([" "] * (i - 1) + [str(i % 10)] + [" "] * (2 * (n - i) - 1) + [str(i % 10)] + [" "] * (i - 1)) for i in range(n - 1, 0, -1)]) if n > 0 else ""
