def is_won(moves):
    for line in lines:
        if all((x in moves for x in line)):
            return True
    return False


def play_OX_3D(moves):
    idx = -1
    players = [{'name': 'O', 'moves': []}, {'name': 'X', 'moves': []}]
    for (i, mov) in enumerate(moves):
        idx = (idx + 1) % 2
        player = players[idx]
        player['moves'].append(list(mov))
        if is_won(player['moves']):
            return f"{player['name']} wins after {i + 1} moves"
    return 'No winner'
