def epidemic(t, n, s, i, b, a): return ([0 for d, r, m, S, I, R, z in [(float(t) / n, 0, i, -1, -1, -1, __import__('itertools'))]], [[0 for S, I, R, s, i, r, m in [(lambda t:t + (max(m, t[4]),))((s, i, r, s - d * b * s * i, i + d * (b * s - a) * i, r + d * i * a))]]for _ in z.takewhile(lambda x:I < i, z.repeat(0))], m)[-1]
