MOD = 10 ** 9 + 7
def power(a, n):
  if n == 0:
    return 1
  if n % 2 == 0:
    return power(a, n // 2) ** 2 % MOD
  return power(a, n - 1) * a


n, m = list(map(int, input().split()))
print(power(power(2, m) + MOD - 1, n) % MOD)

def qp(a,b):
    if b==0:
        return 1
    ans=qp(a,b//2)
    if b%2==1:
        return ans*ans%1000000007*a%1000000007
    else:
        return ans*ans%1000000007

n,m=map(int,input().split())

ans=qp(qp(2,m)-1,n)
print(ans)

