def check(s, k):
    for w in set(s):
        c = s[:k].count(w)
        if c == 0:
            continue

        for i in range(k, len(s)):
            if s[i-k] == w:
                c -= 1
            if s[i] == w:
                c += 1
            if c == 0:
                break

        if c > 0:
            return True

    return False


