    ans = []
    for i in range(k - 1, 0, -1):
        ans.append("PRINT " + s[i])
        ans.append("LEFT")
    ans.append("PRINT " + s[0])
    for i in range(k):
        ans.append("RIGHT")
    for i in range(k, n - 1):
        ans.append("PRINT " + s[i])
        ans.append("RIGHT")
    ans.append("PRINT " + s[n - 1])
    return ans

