import collections

def palindromic(f, l):
    if l % 2 == 0:
        return len(odds(f)) == 0
    else:
        return len(odds(f)) == 1

def odds(f):
    return [k for (k,v) in list(f.items()) if v % 2 == 1]
def evens(f):
    return [k for (k,v) in list(f.items()) if v % 2 == 0 and v > 0]

