def palindrome_set_counts(string):
    if string == "":
        return True

    counts = {}
    for letter in string:
        if letter not in counts:
            counts[letter] = 0
        counts[letter] += 1
        counts[letter] %= 2

    letter_sums = 0
    for key, value in list(counts.items()):
        letter_sums += value

    return letter_sums

