import sys
readline = sys.stdin.readline
N = int(readline())
if N == 1:
    print(0)
else:
    BN = 1
    while BN*2 <= N:
        BN*= 2
    Ans = []
    for i in range(BN.bit_length()):
        d = 1<<i
        for j in range(1, BN+1, 2*d):
            if j+d > BN:
                break
            for k in range(d):
                Ans.append((j+k, j+k+d))
    Ans = Ans + [(N+1-a, N+1-b) for a, b in Ans]
    print(len(Ans))
    print('\n'.join('{} {}'.format(*a) for a in Ans))

n = int(input())

def f(x, a):
    if x == 2:
        return [(a, a+1)]
    else:
        s = []
        for i in range(x // 2):
            s += [(a + i, a + i + x // 2)]
        return f(x // 2, a) + f(x // 2, x // 2 + a) + s


if n == 0 or n == 1:
    print(0)
else:
    x = 1
    for i in range(1, n):

        if 2 * x > n:
            break
        x *= 2
    s1 = f(x, 1)
    s2 = f(x, n - x + 1)
    print(len(s1) + len(s2))
    for i in range(len(s1)):
        print(s1[i][0], s1[i][1])
    for i in range(len(s2)):
        print(s2[i][0], s2[i][1])
