    x = len(pole[0]) % 2
    fir = []
    sec = []
    new = []
    for i in range(b):
        s = ''
        for j in range(a):
            s += pole[j][i]
        if i + 1 <= len(pole[0]) // 2:
            fir.append(s)
            continue
        if i + 1 >= (len(pole[0]) // 2 + x):
            sec.append(s)
    for i in range(len(sec[0])):
        s = ''
        for j in range(len(sec) + len(fir)):
            if j < len(sec):
                s += sec[j][i]
            else:
                s += fir[j - len(sec)][i]
        new.append(s)
    return new
