#Left (inc), Right(exc), bit to check, value to add 
queue = [(0,n,30,0)]
best = 2 ** 30
while queue:
    l, r, b, v = queue.pop()
    if b >= 0:
        mask = 1 << b

        if not mask & seq[l] and mask & seq[r - 1]:
            for i in range(l, r):
                if mask & seq[i]:
                    queue.append((l,i,b - 1, v + mask))
                    queue.append((i,r,b - 1, v + mask))
                    break
        else:
            queue.append((l, r, b - 1, v))
    else:
        best = min(best, v)

print(best)
