def getNumber(x, y):
    nonlocal n

    if n % 2 == 0:
        if (x + y) % 2 == 0:
            start = 0
        else:
            start = n ** 2 // 2

        pos = (x - 1) * n // 2 + (y + 1) // 2
    else:
        t = (x - 1) * n + y + 1
        if t % 2 == 0:
            start = 0
        else:
            start = (n ** 2 + 1) // 2
        pos = t // 2
    return start + pos


