def solve(N, A, B, C, S):
    sol = [None] * N
    wins = 0
    for i in range(N):
        if S[i] == 'S' and A > 0:
            sol[i] = 'R'
            A -= 1
            wins += 1
        if S[i] == 'R' and B > 0:
            sol[i] = 'P'
            B -= 1
            wins += 1
        if S[i] == 'P' and C > 0:
            sol[i] = 'S'
            C -= 1
            wins += 1

    if wins < (N + 1) // 2:
        return None

    for i in range(N):
        if sol[i] is None:
            if A > 0:
                sol[i] = 'R'
                A -= 1
            elif B > 0:
                sol[i] = 'P'
                B -= 1
            else:
                sol[i] = 'S'
                C -= 1

    return ''.join(sol)


