def solve(n, k):
    first = max(1, k - n)
    last = min(k // 2, n)

    return max(0, last - first + k % 2)

def brute(n, k):
    ans = 0
    for a in range(1, n + 1):
        b = k - a
        if a < b and b <= n:
            ans += 1

    return ans

