def numneg(arr):
    s = 0
    for x in arr:
        if x < 0:
            s += 1
    return s
def negmax(arr):
    maxi = float('inf')
    for x in arr:
        if abs(x) < maxi:
            maxi = abs(x)
    return -maxi
def sumabs(arr):
    s = 0
    for x in arr:
        s += abs(x)
    return s
