import sys

def findmax2(nums, n):
    inter = {}
    inter[0] = 0
    inter[1] = nums.get(1, 0)
    for i in range(2, n + 1):
        inter[i] = max(inter[i-2] + nums.get(i, 0), inter[i-1])

    return inter[n]

