t = int(input().rstrip())

for i in range(t):
    n = int(input().rstrip())
    nums = list(map(int, input().rstrip().split()))
    forw = 0
    back = n-1

    for j in range(n):
        if nums[j] >= j:
            forw = j
        else:
            break

    for j in range(1, n+1):
        if nums[-j] >= j-1:
            back = n-j
        else:
            break

    if forw >= back:
        print("Yes")
    else:
        print("No")

def f(a):
    for i in range(len(a)):
        if a[i] < i: return i-1
    return len(a)-1

def solve(a):
    i = f(a)
    j = len(a) - 1 - f(a[::-1])
    return "Yes" if i >= j else "No"


n = int(input())
for i in range(n):
    input()
    a = list(map(int, input().strip().split()))
    print(solve(a))

