def sieve(n):
 arr=[]
 prime = [True for i in range(n+1)]
 p = 2
 while (p * p <= n):
  if (prime[p] == True):
   for i in range(p * 2, n+1, p):
    prime[i] = False
  p += 1
 for p in range(2, n):
  if prime[p]:
   arr.append(p)
 return arr




