def dp(a, n):
    mat = [0 for i in range(n)]
    mat[0] = 1
    for i in range(n):
        mat[i] = 1
        for j in range(i):
            if mat[j] != 0 and a[i] % a[j] == 0:
                mat[i] = max(mat[i], mat[j] + 1)
    return max(mat)


