def get_mini(n, ls):
 ls = sorted(ls)[::-1]
 first_burner = 0
 second_burner = 0
 for i in range(n):
  if first_burner <= second_burner:
   first_burner += ls[i]
  else:
   second_burner += ls[i]
 return max(first_burner, second_burner)


