from decimal import Decimal

T = int(input())
for _ in range(T):
    N = int(input())
    data = dict() 
    for __ in  range(N):
        ci, pi = input().split() 
        data[ci] = pi
    S = list(input())
    for i in range(len(S)):
        if S[i] in data.keys():
            S[i] = data[S[i]] 
    ###
    S = "".join(S)
    if '.' in S:
        S = S.strip('0').rstrip('.')
    else:
        S = S.lstrip('0')
    print(S or '0')

for _ in range(int(input())):
    d={}
    for __ in range(int(input())):
        c,p=map(str,input().split())
        d[c]=p
    s=input()
    k=""
    for i in range(0,len(s)):
        if(s[i] in d.keys()):
            k=k+d[s[i]]
        else:
            k=k+s[i]
     
    if '.' in k:
        k = k.strip('0').rstrip('.') 
